<?php$format = true;function formatTable($par1, $par2) {    global $format;    if ($format)    {        $newText = '<tr style="background-color:#f8f8f8">        <td style="padding:10px;border:#e9e9e9 1px solid"><b>'.$par1.'</b></td>        <td style="padding:10px;border:#e9e9e9 1px solid">'.$par2.'</td>        </tr>';    }    else    {        $newText = '<tr>        <td style="padding:10px;border:#e9e9e9 1px solid"><b>'.$par1.'</b></td>        <td style="padding:10px;border:#e9e9e9 1px solid">'.$par2.'</td>        </tr>';    }    $format = !$format;    return $newText;}if (!empty($_POST)){    $to = "prozrachno.test@yandex.ru"; //Почта получателя    $subject = "Заявка с сайта ".$_SERVER["HTTP_HOST"]; //Загаловок сообщения    $message = "    <html>        <head>            <title>".$subject."</title>        </head>        <div class='results'>";    $message .= formatTable("Имя", $_POST["user_name"]);    $message .= formatTable("Телефон", $_POST["user_phone"]);    $message .= formatTable("E-mail", $_POST["user_email"]);    $message .= formatTable("Адрес доставки", $_POST["user_adress"]);    $message .= formatTable("Транспортная компания", $_POST["user_transfer"]);    $message .= formatTable("Комментарий к заказу", $_POST["user_text"]);    $message = "<table style='width: 100%;'>$message</table>";    $message .= "</div>    </html>"; //Текст нашего сообщения можно использовать HTML теги    $headers = "Content-type: text/html; charset=utf-8 \r\n"; //Кодировка письма    $headers =  'MIME-Version: 1.0' . "\r\n";    $headers .= 'From: Your name <prozrachno.test@yandex.ru>' . "\r\n";    $headers .= 'Content-type: text/html; charset=utf-8' . "\r\n";//    echo "index.php";    $alert_dell = "Заявка успешно отправлена";    echo "<script type='text/javascript'>alert('$alert_dell');</script>";    mail($to, $subject, $message, $headers); //Отправка письма с помощью функции mail}?>